		(TO_CLOB('<?xml version="1.0" encoding="ISO-8859-1" ?><DOCUMENT><ID_DOCUMENT>2005388</ID_DOCUMENT><CLEF_ARRET></CLEF_ARRET><TYPE_ARRET></TYPE_ARRET><NATURE_JURISPRUDENCE></NATURE_JURISPRUDENCE><JURIDICTION></JURIDICTION><CHAMBRE>CR</CHAMBRE><COMPOSITION></COMPOSITION><NUM_DECISION>C2401411</NUM_DECISION><DT_DECISION>22/10/2024</DT_DECISION><SOLUTION>Cassation</SOLUTION><TEXTE_VISE></TEXTE_VISE><RAPPROCHEMENT></RAPPROCHEMENT><PRESIDENT>M. Bonnal (président)</PRESIDENT><CONSRAP>Mme Chaline-Bellamy</CONSRAP><AVOG>M. Tarabeux</AVOG><SOURCE></SOURCE><DOCTRINE></DOCTRINE><CAT_PUB>P</CAT_PUB><TEXTE_ARRET>N° N 24-84.540 F-B
 
