	INTO DOCUMENT (ID_DOCUMENT, CLEF_ARRET, TYPE_ARRET, NATURE_JURISPRUDENCE, JURIDICTION, ID_CHAMBRE, COMPOSITION, NUM_DECISION, DT_DECISION, ID_SOLUTION, TEXTE_VISE, RAPROCHEMENT, PRESIDENT, CONSRAP, AVOG, SOURCE, DOCTRINE, CAT_PUB, IND_CORRIGEJO, IND_VALIDEJO, TYPE_LIEN, XML, DT_CREATION, AUT_CREATION, DT_MODIF, AUT_MODIF, IND_BICC, LIEN_BICC, IND_RCC, LIEN_RCC, IND_WWW, LIEN_WWW, SOURCEBIS, DT_ANO, IND_ANO, XMLA, AUT_ANO, DT_ENVOI_DILA, DT_MODIF_ANO, XMLT, XMLAT, IND_VERIF_ARRET, IND_VERIF_RAPPORT, IND_VERIF_AVIS, AUT_ANO_RAPPORT, DT_ANO_RAPPORT, AUT_ANO_AVIS, DT_ANO_AVIS, COM_PRESSE, NOTE_EXPL, REF_ARRET_CEDH, LIEN_ARRET_CEDH, IND_PM, IND_PHYM_GEOM, IND_ADRESSE, IND_DT_NAISSANCE, IND_DT_DECE, IND_DT_MARIAGE, IND_IMMATRICULATION, IND_CADASTRE, IND_CHAINE, IND_COORDONNEE_ELECTRONIQUE, IND_PRENOM_PROFESSIONEL, IND_NOM_PROFESSIONEL, OCCULTATION_SUPPLEMENTAIRE, ID_FORMATION, IND_BULLETIN, IND_RAPPORT, IND_LETTRE, IND_COMMUNIQUE) VALUES (
