		(TO_CLOB('<?xml version="1.0" encoding="ISO-8859-1" ?><DOCUMENT><ID_DOCUMENT>2004867</ID_DOCUMENT><CLEF_ARRET></CLEF_ARRET><TYPE_ARRET></TYPE_ARRET><NATURE_JURISPRUDENCE></NATURE_JURISPRUDENCE><JURIDICTION></JURIDICTION><CHAMBRE>CR</CHAMBRE><COMPOSITION></COMPOSITION><NUM_DECISION>C2401300</NUM_DECISION><DT_DECISION>23/10/2024</DT_DECISION><SOLUTION>Cassation partielle</SOLUTION><TEXTE_VISE></TEXTE_VISE><RAPPROCHEMENT></RAPPROCHEMENT><PRESIDENT>M. Bonnal (président)</PRESIDENT><CONSRAP>M. Gouton</CONSRAP><AVOG>M. Aldebert</AVOG><SOURCE></SOURCE><DOCTRINE></DOCTRINE><CAT_PUB>D</CAT_PUB><TEXTE_ARRET>N° T 24-80.175 F-D
 
